import { Editora } from '../modelo/Editora';

export const editoras: Editora[] = [ 
     {    "codEditora": 100,    "nome": "Alta Books"  }, 
     {    "codEditora": 200,    "nome": "Pearson"  },  
     {    "codEditora": 300,    "nome": "Addison Wesley"  }
];
export default  class ControleEditora {
    private editoras: Editora[];
  
    constructor() {
      this.editoras = [];
    }
  
    getNomeEditora(codEditora:number) {
        const editora = this.editoras.filter((e) => e.codEditora === codEditora)[0];
        return editora ? editora.nome: null;
      }
  
    getEditoras(): Editora[] {
      return this.editoras;
    }
  }

export{}
