import { Livro } from '../modelo/Livro';

const livros: Livro[] = [ 
    {
        "codigo": 1,
        "codEditora": 100,
        "titulo": "A Origem das Espécies",
        "resumo": "Obra fundamental para a teoria da evolução das espécies",
        "autores": ["Charles Darwin"]
      },
      {
        "codigo": 2,
        "codEditora": 200,
        "titulo": "Guerra e Paz",
        "resumo": "Um dos grandes romances da literatura mundial",
        "autores": ["Liev Tolstói"]
      },
      {
        "codigo": 3,
        "codEditora": 300,
        "titulo": "Dom Casmurro",
        "resumo": "Um dos grandes clássicos da literatura brasileira",
        "autores": ["Machado de Assis"]
      }
    ]

class ControleLivro {
    private livros: Livro[];
  
    constructor() {
      this.livros = [];
    }
  
    obterLivros(): Livro[] {
      return this.livros;
    }
  
    incluir(livro: Livro): void {
        const maiorCodigo = this.livros.reduce((maior, livro) => {
            return livro.codigo > maior ? livro.codigo : maior;
          }, 0);
    }
  
    excluir(codigo: number): void {
        const index = this.livros.findIndex(livro => livro.codigo === codigo);

        if (index !== -1) {
          // remove o livro do vetor
          this.livros.splice(index, 1);
        }
    }
  }


export{}
