import { Editora } from '../modelo/Editora';

export const editoras: Editora[] = [ 
     {    "codEditora": 100,    "nomeEditora": "Alta Books"  }, 
     {    "codEditora": 200,    "nomeEditora": "Pearson"  },  
     {    "codEditora": 300,    "nomeEditora": "Addison Wesley"  }
];
export class ControleEditora {
  private editoras: Editora[];

  constructor(editoras: Editora[]) {
    this.editoras = editoras;
  }

  getNomeEditora(codEditora: number) {
    const editora = this.editoras.filter((e) => e.codEditora === codEditora)[0];
    return editora ? editora.nomeEditora : null;
  }

  getEditoras(): Editora[] {
    return this.editoras;
  }

  setEditoras(editoras: Editora[]) {
    this.editoras = editoras;
  }
}
